//import 'dart:convert';

import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:flutter/widgets.dart';

/// Nombre de la screen snack_case
/// Mismo nombre de la clase PascalCase
class PhotoAlbumScreen extends StatelessWidget {
  PhotoAlbumScreen({super.key});

  static const String routeName = '/photo-album';

  final photoUrl =
      "https://scontent.fuio1-2.fna.fbcdn.net/v/t1.6435-1/61049095_2286442614756155_2690540833324662784_n.jpg?stp=dst-jpg_s480x480&_nc_cat=101&ccb=1-7&_nc_sid=5f2048&_nc_ohc=g8YPGicZrpwQ7kNvgG_zZIS&_nc_ht=scontent.fuio1-2.fna&oh=00_AYBvfkaKg2eV1dFPeFWahUet9I1bjF7s0zB-lkqyz4HE3g&oe=667DE513";

  //// Se genera una lista de imagenes aleatorias que nos ayuda a crear nuestro album
  final listImages =
      List.generate(50, (index) => 'https://picsum.photos/id/$index/1200');

  /// Puede venir desde un EP
  /// {
  ///   "base_64": "iVBORw0KGgoAAAANS"
  /// }
  // final imageBase64 =
  //     "";

  @override
  Widget build(BuildContext context) {
    /// theme
    // final sizeOfDevice = Theme.of(context).wi
    /// Media query
    final sizeOfDevice2 = MediaQuery.of(context).size.width;
    print("Ancho del device: ${sizeOfDevice2}");

    return Scaffold(
      appBar: AppBar(
        /// Title de el app bar, generalmente va a ser un widget de tipo Text como hijo
        title: const Text("Album de fotos"),

        /// recibe un bool si es true el title se centra si es false se pone en la izquierda
        centerTitle: false,

        /// Recibe Lista widgets -> Siempre van a estar a la derecha del AppBar
        actions: const [
          /// Widget que nos va a permitir crear Avatares
          ///
          /// Container -> tiene varias propiedades mutables -> widget no sea constante
          /// Padding -> es optimo porque solo maneja las separaciones
          /// SizedBox -> es optimo porque solo ocupa un espacio
          ///
          Padding(
            padding: EdgeInsets.only(right: 8.0),
            child: CircleAvatar(
              /// Utilizar el child para asignarle un contenido al circle avatar, puede ser texto u otros widgets
              /// Iniciales max 2 letras
              /// style nos permite definir el estilo del texto TextStyle
              child: Text(
                "RC",
                style: TextStyle(
                  /// color -> color del texto
                  color: Colors.purple,

                  /// fontSize -> el tamaño de la fuente
                  fontSize: 15,

                  ///fontWeight -> peso de la fuente
                  /// FontWeight.bold -> negrillas
                  /// FontWeight.normal -> normal
                  /// FontWeight.w100 - w900 -> un rango en representación del peso
                  fontWeight: FontWeight.w600,
                ),
              ),

              /// recibe un double y esto define el tamaño del radio del círculo
              // radius: 60,
            ),
          ),
        ],
      ),

      /// El hijo de un ListView(tiene tamaño infinito) - debe tener un tamaño finito
      ///  ListView -> infinito en altura
      ///  GridView -> infinito en altura -> 1. dandole una altura -> sizedbox -> container
      ///     2. shrinkWrap default false -> cambiarle a verdadero (total de los items)
      body: ListView(
        padding: const EdgeInsets.all(16.0),
        children: [
          Row(
            children: [
              /// Voy a crear un avatar que renderiza una imagen remota
              CircleAvatar(
                /// Forma #1
                /// Recibe un image provider
                /// NetworkImage -> un proveedor de imagen que nos permite utilizar una url remota
                /// scale -> permite definir la escala de la imagen pero en relación al peso para optimizar el llamado a la url
                backgroundImage: NetworkImage(photoUrl),

                /// radius -> es lo que define el tamaño del círculo
                radius: 50,

                /// Forma #2
                /// Utilizando el child del circle avatar
                /// Crear widgets de imagenes -> Image.network
                /// Forzar el borde circular de un widget se utiliza el Widget
                /// ClipRRect
                child: ClipRRect(
                  // Radio para que el widget se recorte
                  // Se llama como BorderRadiusGeometry -> BorderRadius
                  // BorderRadius.circular(50) -> se recorta en función de un radio
                  borderRadius: BorderRadius.circular(50),

                  /// Hijo o el widget que se quiere recortar
                  child: Image.network(photoUrl),
                ),
              ),

              /// Un espacio que separa la imagen del otro widget
              const SizedBox(width: 16),

              /// Los nombres y un title
              /// Vamos a usar el expanded para que nuestro widget se adapte a dispositivos pequeños
              const Expanded(
                child: Column(
                  /// Para controlar la alineación en el eje horizontal
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    Text(
                      "Richar Cangui",
                      style: TextStyle(
                        /// Peso de la letra
                        fontWeight: FontWeight.bold,

                        /// Tamaño de la letra
                        fontSize: 20,
                      ),
                    ),
                    Text("Mis recuerdos"),
                  ],
                ),
              ),
            ],
          ),

          /// Necesitamos un separador para que la lista no se encuentre tan pegada
          const SizedBox(height: 16),

          /// Consumo de una imagen base 64
          /// dart:convert -> base64Decode -> recibe un string y lo convierte en bytes
          /// memory -> imagenes que toman desde el dispositivo
          /// bytes -> manera más rapida de dibujar las imagenes
          /// memory -> no se conserva el estado -> no maneja un cache
          // Image.memory(base64Decode(imageBase64)),

          /// Acceder a una imagen local dentro del compilado como asset
          /// Se accede desde la ruta raiz, en este caso los assets
          // Image.asset("assets/loading.gif"),

          /// Grillas -> GridView
          GridView.count(
            /// Maneja el scroll
            /// Siempre desactivar el scroll en el widget interno scrolleable
            physics: const NeverScrollableScrollPhysics(),

            crossAxisCount: sizeOfDevice2 > 768 ? 5 : 3,

            /// Hasta ahora no hemos usado esta propiedad, pero está presente en todas las listas
            /// el shrinkWrap hace que la lista pierda las propiedades de performance y genera que
            /// todos los elementos se rendericen a la vez, esto produce que un ListView se convierta
            /// conceptualmente hablando en un single child scroll view.
            /// Al ser shrinkWrap=true, nuestro size ya no es infinito, se convierte en finito, por lo que
            /// la lista ya puede ubicarse dentro de otra lista
            shrinkWrap: true,
            // children: [
            //   Card(
            //     /// El clip permite hacer un corte a la foto para que se ajuste al formato de la tarjeta
            //     /// Se puede decir que es parecido al ClipRRect
            //     /// clipBehavior -> recorte que va a necesitar el widget interno
            //     clipBehavior: Clip.hardEdge,
            //     // child: ClipRRect(
            //     //   borderRadius: BorderRadius.circular(100),
            //     //   child: Image.network(photoUrl)
            //     //   ),
            //     child: Image.network(photoUrl),
            //   )
            // ],
            /// image -> es la url en la posición en específico
            /// [1,2,3,4,5,6,7,8,9] -> 1 o 2 o 3
            children: listImages.map((image) {
              return Card(
                clipBehavior: Clip.hardEdge,

                /// Widget clasico para renderizar una imagen
                // child: Image.network(image),
                /// Darle al usuario un feedback visual
                /// FadeInImage
                child: FadeInImage(
                    // placeholder: Image.asset(name),
                    placeholder: const AssetImage("assets/loading.gif"),
                    image: NetworkImage(image)),
              );
            }).toList(),
          ),
        ],
      ),
    );
  }
}